import React from 'react';
import ReactDOM from 'react-dom';
import injectTapEventPlugin from 'react-tap-event-plugin';
import Main from './Main'; // Our custom react component

// Needed for onTouchTap
// Can go away when react 1.0 release
// Check this repo:
// https://github.com/zilverline/react-tap-event-plugin
injectTapEventPlugin();

// Render the main app react component into the app div.
// For more details see: https://facebook.github.io/react/docs/top-level-api.html#react.render
ReactDOM.render(<Main />, document.getElementById('app'));

import React from 'react';
import { pink300 } from 'material-ui/styles/colors';
import getMuiTheme from 'material-ui/styles/getMuiTheme';
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import TopBar from './my/TopBar';
import CountGraph from './my/CountGraph';
import ItemBox from './my/ItemBox';

const muiTheme = getMuiTheme({
  palette: {
    accent1Color: pink300,
  },
});

function Main() {
  return (
    <MuiThemeProvider muiTheme={muiTheme}>
      <div>
        <TopBar />
        <div>
          <CountGraph />
          <ItemBox />
        </div>
      </div>
    </MuiThemeProvider>
  );
}

Main.childContextTypes = {
  muiTheme: React.PropTypes.object.isRequired,
};

export default Main;

var invariant = require('fbjs/lib/invariant');
var defaultClickRejectionStrategy = require('./defaultClickRejectionStrategy');

var alreadyInjected = false;

module.exports = function injectTapEventPlugin (strategyOverrides) {
  strategyOverrides = strategyOverrides || {}
  var shouldRejectClick = strategyOverrides.shouldRejectClick || defaultClickRejectionStrategy;

  if (process.env.NODE_ENV !== 'production') {
    invariant(
      !alreadyInjected,
      'injectTapEventPlugin(): Can only be called once per application lifecycle.\n\n\
It is recommended to call injectTapEventPlugin() just before you call \
ReactDOM.render(). If you are using an external library which calls injectTapEventPlugin() \
itself, please contact the maintainer as it shouldn\'t be called in library code and \
should be injected by the application.'
    )
  }

  alreadyInjected = true;

  require('react/lib/EventPluginHub').injection.injectEventPluginsByName({
    'TapEventPlugin':       require('./TapEventPlugin.js')(shouldRejectClick)
  });
};

import React from 'react';
import axios from 'axios';
import { grey200 } from 'material-ui/styles/colors';
import CircularProgress from 'material-ui/CircularProgress';

class CountGraph extends React.Component {
  constructor(props, context) {
    super(props, context);

    this.state = {
      fetched: false,
      info: {
        label: '',
        count_unread: 0,
        count_archive: 0,
      },
    };
  }

  componentDidMount() {
    this.loadFromServer();
  }

  loadFromServer() {
    axios.get('/info')
      .then((response) => {
        const unread  = response.data.count_unread;
        const archive = response.data.count_archive;
        this.setState({
          fetched: true,
          info: {
            label: `${unread} / ${unread + archive}`,
            count_unread: unread,
            count_archive: archive,
          },
        });
      })
      .catch((response) => {
        console.error('/info', response);
      });
  }

  render() {
    const info = this.state.info;
    const deno = info.count_unread + info.count_archive;
    const rate = info.count_unread / deno * 100;
    const styles = {
      outer: {
        backgroundColor: grey200,
      },
      inner: {
        width: `${rate}%`,
        backgroundColor: this.context.muiTheme.palette.accent1Color,
      },
    };
    let cp = this.state.fetched ? null : <CircularProgress size={0.5} />;
    return (
      <div className="countGraph" style={styles.outer}>
        {cp}
        <div className="countGraph-inner" style={styles.inner}>
          <span>{this.state.info.label}</span>
        </div>
      </div>
    );
  }
}

CountGraph.contextTypes = {
  muiTheme: React.PropTypes.object.isRequired,
};

export default CountGraph;

import React from 'react';
import AppBar from 'material-ui/AppBar';
import IconButton from 'material-ui/IconButton';
import IconMenu from 'material-ui/IconMenu';
import MenuItem from 'material-ui/MenuItem';
import MoreVertIcon from 'material-ui/svg-icons/navigation/more-vert';
import SyncDisabled from 'material-ui/svg-icons/notification/sync-disabled';
import OpenInNew from 'material-ui/svg-icons/action/open-in-new';

class TopBar extends React.Component {
  openPocket() {
    window.open('https://getpocket.com');
  }

  exitApp() {
    window.location = '/disconnect';
  }

  render() {
    return (
      <AppBar
        title="Chase"
        showMenuIconButton={false}
        iconElementRight={
          <IconMenu
            iconButtonElement={
              <IconButton><MoreVertIcon /></IconButton>
            }
            targetOrigin={{ horizontal: 'right', vertical: 'top' }}
            anchorOrigin={{ horizontal: 'right', vertical: 'top' }}
          >
            <MenuItem
              primaryText="Pocket"
              leftIcon={<OpenInNew />}
              onTouchTap={this.openPocket}
            />
            <MenuItem
              primaryText="Disconnect"
              leftIcon={<SyncDisabled />}
              onTouchTap={this.exitApp}
            />
          </IconMenu>
        }
      />
    );
  }
}

export default TopBar;

import React from 'react';
import axios from 'axios';
import ItemList from './ItemList';

class ItemBox extends React.Component {
  constructor(props, context) {
    super(props, context);

    this.state = {
      thumbed: false,
      list: [],
    };
  }

  componentDidMount() {
    this.loadFromServer();
  }

  loadFromServer() {
    axios.get('/retrieve')
      .then((response) => {
        this.setState({ list: response.data });
      })
      .catch((response) => {
        console.error('/retrieve', response);
      });
  }

  render() {
    const styles = {
      itemBox: {
        maxWidth: 800,
        margin: '15px auto',
      },
    };
    return (
      <div className="itemBox" style={styles.itemBox}>
        <ItemList data={this.state} />
      </div>
    );
  }
}

export default ItemBox;

